def frekans(data, columns, n_label='NONE'):
    for col in columns:
        fq_encode = data[col].value_counts(dropna=False).to_dict()
        data[col + '_Fr'] = data[col].map(fq_encode)
        data = data.drop(col, axis=1)
    return data

